import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { fetchAllocation } from '../features/allocationSlice';

const DepartmentForm = () => {
  // Initialize demand with 7 elements, one for each day of the week
  const [departmentData, setDepartmentData] = useState({ demand: Array(7).fill(0) });
  const dispatch = useDispatch();

  const handleDemandChange = (value, index) => {
    // Update the demand for the specific day
    setDepartmentData((prevData) => {
      const newDemand = [...prevData.demand];
      newDemand[index] = Number(value);
      return { ...prevData, demand: newDemand };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(fetchAllocation(departmentData));
  };

  return (
    <form onSubmit={handleSubmit}>
      {departmentData.demand.map((value, index) => (
        <div key={index}>
          <label>
            Day {index + 1} demand:
            <input
              type="number"
              value={value}
              onChange={(e) => handleDemandChange(e.target.value, index)}
              min="0"
            />
          </label>
        </div>
      ))}
      <button type="submit">Calculate Allocation</button>
    </form>
  );
};

export default DepartmentForm;
