import React from 'react';
import { Formik, Form, useField } from 'formik';
import * as Yup from 'yup';

// Custom Input component for Formik
const MyTextInput = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input {...field} {...props} />
      {meta.touched && meta.error ? <div className="error">{meta.error}</div> : null}
    </>
  );
};

// Validation Schema using Yup
const validationSchema = Yup.object({
  firstName: Yup.string().required('Required'),
  lastName: Yup.string().required('Required'),
  // ... Include other fields in the schema as needed ...
});

function AssociateForm() {
  // Initial values for the form
  const initialValues = {
    firstName: '',
    lastName: '',
    // ... Include other initial values as needed ...
  };

  // onSubmit function for additional debugging
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    console.log('Attempting to submit form with values:', values);
    // ... Dispatch and other form handling logic ...
  };

  return (
    <div>
      <h3>Add an Associate!</h3>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={onSubmit}
      >
        {({ isSubmitting }) => (
          <Form>
            <MyTextInput label="First Name" name="firstName" type="text" />
            <MyTextInput label="Last Name" name="lastName" type="text" />
            {/* ... Include other form fields as needed ... */}
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default AssociateForm;
