import React from 'react'
import {useDispatch} from 'react-redux'
import {deleteWorker} from '../features/workersSlice'
import Metric from "./Metric";


function AssociateCard({worker}) {
  const dispatch = useDispatch();

  function handleWorkerDelete(){
    fetch(`http://localhost:5555/associate_metrics/${worker.id}`,{
      method:'DELETE'
    })
    .then(r=>r.json())
    .then(data=>{
      dispatch(deleteWorker(worker.id));
    })
  }



    return (
      <div className="ui card">
  <div className="content">
    <div className="header">{worker.first_name}</div>
    <div className="header">{worker.last_name}</div>
  </div>
  <div className="content">
    <h4 className="ui sub header">Activity</h4>
    <div className="ui small feed">
      <div className="event">
        <div className="content">
          <div className="summary">
             <a>Uptime</a> {worker.uptime}
          </div>
        </div>
      </div>
      <div className="event">
        <div className="content">
          <div className="summary">
             <a>Cases Per Hour</a> {worker.casesPerHour}
          </div>
        </div>
      </div>
      <div className="event">
        <div className="content">
          <div className="summary">
             <a>Attendance</a> {worker.attendance}
          </div>
        </div>
      </div>
    </div>
  </div>
  <div className="extra content">
    <button onClick={handleWorkerDelete} className="ui button">Delete</button>
  </div>
</div>
    );
  }

  export default AssociateCard
