import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchWorkers, updateAssociate } from '../features/workersSlice';

const AssociatesTable = () => {
  const dispatch = useDispatch();
  const { workers, status, error } = useSelector(state => state.workers);
  const [editingWorker, setEditingWorker] = useState(null);
  const [tempSchedules, setTempSchedules] = useState({});
  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

  useEffect(() => {
    if (status === 'idle') {
      dispatch(fetchWorkers());
    }
  }, [status, dispatch]);

  const handleClick = (worker) => {
    setEditingWorker(worker);
    setTempSchedules(worker.schedules);
  };

  const handleScheduleChange = (day, startOrEnd, value) => {
    setTempSchedules({
      ...tempSchedules,
      [day]: {
        ...tempSchedules[day],
        [startOrEnd]: value,
      },
    });
  };

  const saveChanges = () => {
    // Dispatch an action to update the worker's schedule in the backend
    dispatch(updateAssociate({ ...editingWorker, schedules: tempSchedules }));
    setEditingWorker(null); // Exit editing mode
  };

  if (status === 'loading') return <div>Loading...</div>;
  if (status === 'failed') return <div>Error: {error}</div>;

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Department</th>
            {daysOfWeek.map(day => <th key={day}>{day}</th>)}
          </tr>
        </thead>
        <tbody>
          {workers.map(worker => (
            <tr key={worker.id} onClick={() => handleClick(worker)}>
              <td>{`${worker.first_name} ${worker.last_name}`}</td>
              <td>{worker.department.name}</td>
              {daysOfWeek.map(day => (
                <td key={day}>
                  {editingWorker?.id === worker.id ? (
                    <div>
                      <input
                        type="time"
                        value={tempSchedules[day]?.shift_start || ''}
                        onChange={e => handleScheduleChange(day, 'shift_start', e.target.value)}
                      />
                      -
                      <input
                        type="time"
                        value={tempSchedules[day]?.shift_end || ''}
                        onChange={e => handleScheduleChange(day, 'shift_end', e.target.value)}
                      />
                    </div>
                  ) : (
                    `${tempSchedules[day]?.shift_start || ''} - ${tempSchedules[day]?.shift_end || ''}`
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      {editingWorker && <button onClick={saveChanges}>Save Changes</button>}
    </div>
  );
};

export default AssociatesTable;
