import React from "react";
import AssociateCard from "./AssociateCard";
import { Card } from "semantic-ui-react";
import {useDispatch,useSelector} from 'react-redux'
import {deleteWorker} from '../features/workersSlice'

function AssociateRoster() {
  const dispatch = useDispatch();
  const workers = useSelector((state) => state.workers);
  const handleDelete = (workerId)=>{
    dispatch(deleteWorker(workerId))
  }
  const workersList = workers.map(worker=>{
        return <AssociateCard key={worker.id} worker={worker} handleDelete={handleDelete}/>
    })
    const forkLifts = workers.filter(forklift=>forklift.job_class=='Putaway Forklift')
    const forkLiftCards = forkLifts.map(forklift=>{
      return <AssociateCard key = {forklift.id} worker={forklift} handleDelete={handleDelete}/>
    })

  return (
    <div>
    <h2>Selectors</h2>
    <Card.Group itemsPerRow={4}>
      {workersList}
    </Card.Group>
    <h2>Forklifts</h2>
    <Card.Group itemsPerRow={4}>
      {forkLifts}
    </Card.Group>
    </div>
  );
}

export default AssociateRoster;
