import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './AssociatesTable.css'; // Ensure you have this CSS for basic styling
// Import your updateAssociate action from your slice
import { updateAssociate } from '../features/workersSlice';

const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

const AssociatesTable = () => {
  const [showModal, setShowModal] = useState(false);
  const [editingWorker, setEditingWorker] = useState(null);
  const dispatch = useDispatch();
  const workers = useSelector((state) => state.workers.workers);

  const handleClick = (worker) => {
    console.log('Row clicked', worker);
    setEditingWorker(worker);
    setShowModal(true);
  };

  const saveSchedule = (workerId, selectedDays) => {
    dispatch(updateAssociate({ workerId, schedules: selectedDays }));
    setShowModal(false); 
  };



  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Department</th>
            {daysOfWeek.map(day => (
              <th key={day}>{day}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {workers.map(worker => (
            <tr key={worker.id} onClick={() => handleClick(worker)}>
              <td>{worker.first_name} {worker.last_name}</td>
              <td>{worker.department.name}</td>
              {daysOfWeek.map(day => (
                <td key={day}>
                  {worker.schedules.includes(day) ? '✓' : 'X'}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      {showModal && (
        <ScheduleModal
          isOpen={showModal}
          onClose={() => setShowModal(false)}
          onSave={saveSchedule}
          initialDays={editingWorker?.schedules || []}
          workerId={editingWorker?.id}
        />
      )}
    </div>
  );
};

const ScheduleModal = ({ isOpen, onClose, onSave, initialDays = [], workerId }) => {
  const [selectedDays, setSelectedDays] = useState(initialDays);

  const handleDayChange = (day) => {
    setSelectedDays(currentDays =>
      currentDays.includes(day)
        ? currentDays.filter(d => d !== day)
        : [...currentDays, day]
    );
  };

  const handleSave = () => {
    onSave(workerId, selectedDays);
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>Edit Schedule</h2>
        {daysOfWeek.map(day => (
          <div key={day}>
            <label>
              <input
                type="checkbox"
                checked={selectedDays.includes(day)}
                onChange={() => handleDayChange(day)}
              /> {day}
            </label>
          </div>
        ))}
        <button onClick={handleSave}>Save</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default AssociatesTable;
