// Make sure to include React in your imports if you're using JSX.
import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './AssociatesTable.css'; // Ensure your styles for the table and modal are correctly imported.
import { updateAssociate } from '../features/workersSlice';

const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

const AssociatesTable = () => {
  const [showModal, setShowModal] = useState(false);
  const [editingWorker, setEditingWorker] = useState(null);
  const dispatch = useDispatch();
  const workers = useSelector((state) => state.workers.workers);

  const handleClick = (worker) => {
    console.log('Row clicked', worker);
    setEditingWorker(worker);
    setShowModal(true);
  };

  const saveSchedule = (workerId, selectedDays) => {
    // Assuming updateAssociate is a redux action you dispatch
    dispatch(updateAssociate({ workerId, schedules: selectedDays }));
    setShowModal(false); // Close the modal after saving
  };

  return (
    <div>
      {/* Your table structure remains unchanged */}
      {showModal && (
        <ScheduleModal
          isOpen={showModal}
          onClose={() => setShowModal(false)}
          onSave={saveSchedule}
          initialDays={Object.keys(editingWorker?.schedules || {})}
          workerId={editingWorker?.id}
        />
      )}
    </div>
  );
};

// Assuming the ScheduleModal is a functional component receiving props
const ScheduleModal = ({ isOpen, onClose, onSave, initialDays = [], workerId }) => {
  const [selectedDays, setSelectedDays] = useState(initialDays);

  const handleDayChange = (day) => {
    // Toggle day in or out of the array based on its current presence
    setSelectedDays(currentDays =>
      currentDays.includes(day)
        ? currentDays.filter(d => d !== day)
        : [...currentDays, day]
    );
  };

  const handleSave = () => {
    onSave(workerId, selectedDays);
    onClose(); // Close modal on save
  };

  if (!isOpen) return null;

  // Ensure your modal and overlay have styles that make them visible and appropriately layered over the page content
  return (
    <div className="modal-overlay" style={{ position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, backgroundColor: 'rgba(0,0,0,0.5)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <div className="modal-content" style={{ background: 'white', padding: 20, borderRadius: 5 }}>
        <h2>Edit Schedule</h2>
        {daysOfWeek.map(day => (
          <div key={day}>
            <label>
              <input
                type="checkbox"
                checked={selectedDays.includes(day)}
                onChange={() => handleDayChange(day)}
              /> {day}
            </label>
          </div>
        ))}
        <button onClick={handleSave}>Save</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default AssociatesTable;
