import React from 'react'
import {Card} from 'semantic-ui-react'
import {useDispatch} from 'react-redux'
import {deleteWorker} from '../features/workersSlice'

function AssociateCard({worker}) {
  const dispatch = useDispatch();

  function handleWorkerDelete(){
    fetch(`http://localhost:5555/selectors_metrics/${worker.id}`,{
      method:'DELETE'
    })
    .then(r=>r.json())
    .then(data=>{
      dispatch(deleteWorker(worker.id));
    })
  }

    return (
      <div className="ui card">
  <div className="content">
    <div class="header">{worker.name}</div>
  </div>
  <div class="content">
    <h4 class="ui sub header">Activity</h4>
    <div class="ui small feed">
      <div class="event">
        <div class="content">
          <div class="summary">
             <a>Uptime</a> {worker.uptime}
          </div>
        </div>
      </div>
      <div class="event">
        <div class="content">
          <div class="summary">
             <a>Cases Per Hour</a> {worker.casesPerHour}
          </div>
        </div>
      </div>
      <div class="event">
        <div class="content">
          <div class="summary">
             <a>Attendance</a> {worker.attendance}
          </div>
        </div>
      </div>
    </div>
  </div>
  <div class="extra content">
    <button onClick={handleWorkerDelete} class="ui button">Delete</button>
  </div>
</div>
    );
  }

  export default AssociateCard
