import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { Link, Route, Switch } from 'react-router-dom';
import MasterOperatingPlan from './MasterOperatingPlan';
import About from './About';
import ExecutiveSummary from './ExecutiveSummary';
import AssociateForm from './AssociateForm';
import AllocationSummary from './AllocationSummary';
import ScheduleForm from './ScheduleForm';
import AssociatesTable from './AssociatesTable';
import { fetchWorkers } from '../features/workersSlice';
import '../index.css';

function App() {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchWorkers());
  }, [dispatch]);

  return (
    <div className="app-container">
      {/* Navigation buttons */}
      <div className="bg-neutral text-neutral-content flex justify-between px-4 py-2">
        <Link to="/" className="btn btn-ghost">Roster</Link>
        <Link to="/employeeform" className="btn btn-ghost">Add Associate</Link>
        <Link to="/executivesummary" className="btn btn-ghost">Executive Summary</Link>
        <Link to="/about" className="btn btn-ghost">About</Link>
        <Link to="/associatetable" className="btn btn-ghost">Associate Schedules</Link>
        <Link to="/schedulebuilder" className="btn btn-ghost">Schedule Builder</Link>
        <Link to="/allocationsummary" className="btn btn-ghost">Allocation Summary</Link>
      </div>

      {/* Routes */}
      <Switch>
        <Route exact path="/executivesummary" component={ExecutiveSummary}/>
        <Route exact path="/about" component={About} />
        <Route exact path="/employeeform" component={AssociateForm} />
        <Route exact path="/" component={MasterOperatingPlan} />
        <Route exact path="/associatetable" component={AssociatesTable} />
        <Route exact path="/schedulebuilder" component={ScheduleForm} />
        <Route exact path="/allocationsummary" component={AllocationSummary} />
      </Switch>
    </div>
  );
}

export default App;
