import React from 'react';
import { useDispatch } from 'react-redux';
import { deleteWorker } from '../features/workersSlice';
import Metric from "./Metric";

function AssociateCard({ worker }) {
  const dispatch = useDispatch();

  function handleWorkerDelete() {
    fetch(`http://localhost:5555/associate_metrics/${worker.id}`, {
      method: 'DELETE',
    })
      .then(r => r.json())
      .then(data => {
        dispatch(deleteWorker(worker.id));
      });
  }

  // Ensure that worker.metrics is defined and is an array before mapping over it
  const workerMetrics = Array.isArray(worker.metrics) ? worker.metrics.map(metric => {
    // Ensure you have a unique key for each Metric. If metric doesn't have an id, you might use another unique attribute
    return <Metric key={metric.metric_name} metric={metric} />;
  }) : null;

  return (
    <div className="ui card">
      <div className="content">
        <div className="header">{worker.first_name} {worker.last_name}</div>
      </div>
      <div className="content">
        <h4 className="ui sub header">Metrics</h4>
        <div className="ui small feed">
          {workerMetrics || <div>No metrics available.</div>}
        </div>
      </div>
      <div className="extra content">
        <button onClick={handleWorkerDelete} className="ui button">Delete</button>
      </div>
    </div>
  );
}

export default AssociateCard;
