import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSchedule } from '../features/scheduleSlice';

function ScheduleForm() {
  const [dailyDemand, setDailyDemand] = useState([0, 0, 0, 0, 0, 0, 0]);
  const dispatch = useDispatch();
  const schedule = useSelector((state) => state.schedule.data); // Assuming your slice structure

  const error = useSelector((state) => state.schedule.error);

  const handleInputChange = (e, index) => {
    const newDailyDemand = [...dailyDemand];
    newDailyDemand[index] = Number(e.target.value);
    setDailyDemand(newDailyDemand);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(fetchSchedule({ daily_demand: dailyDemand }));
  };

  return (
    <div>
      <h1>Schedule Generator</h1>
      {error && <p>Error: {error}</p>}
      <form onSubmit={handleSubmit}>
        {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map((day, index) => (
          <div key={day}>
            <label>
              {day}:
              <input
                type="number"
                value={dailyDemand[index]}
                onChange={(e) => handleInputChange(e, index)}
              />
            </label>
          </div>
        ))}
        <button type="submit">Generate Schedule</button>
      </form>

      {schedule && (
        <table>
          <thead>
            <tr>
              <th>Shift</th>
              {Object.keys(schedule['Shift: Monday']).map((day) => (
                <th key={day}>{day}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {Object.entries(schedule).map(([shift, days]) => (
              <tr key={shift}>
                <td>{shift}</td>
                {Object.values(days).map((count, index) => (
                  <td key={index}>{count}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ScheduleForm;
