import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

export const fetchSchedule = createAsyncThunk(
  'schedule/fetchSchedule',
  async (staffNeeds, { rejectWithValue }) => {
    try {
      const response = await fetch('http://127.0.0.1:5555/generate_schedule', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(staffNeeds)
      });
      console.log('Response:', response);
      const data = await response.json();
      console.log('Data received:', data);
      return data;
    } catch (error) {
      console.error('Error fetching schedule:', error);
      return rejectWithValue(error.message);
    }
  }
);


const scheduleBuildSlice = createSlice({
  name: 'schedule',
  initialState: {
    data: null,
    status: 'idle',
    error: null
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchSchedule.pending, (state) => {
        console.log('fetchSchedule pending');
        state.status = 'loading';
      })
      .addCase(fetchSchedule.fulfilled, (state, action) => {
        console.log('fetchSchedule fulfilled with payload:', action.payload);
        state.status = 'succeeded';
        state.data = action.payload;
      })
      .addCase(fetchSchedule.rejected, (state, action) => {
        console.log('fetchSchedule rejected with error:', action.error);
        state.status = 'failed';
        state.error = action.payload;
      });
  }
});

export default scheduleBuildSlice.reducer;
