import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { updateWorkerSchedule } from '../features/workersSlice'; // Assume existence of this action

const AssociatesTable = () => {
  const workers = useSelector((state) => state.workers.workers); // Adjust to your actual state slice
  const dispatch = useDispatch();

  const handleScheduleChange = (event, workerId, day) => {
    const updatedTime = event.target.value;
    const payload = {
      id: workerId,
      day: day,
      updatedTime: updatedTime,
    };
    dispatch(updateWorkerSchedule(payload)); // Dispatch action to update worker's schedule
  };

  return (
    <div className="associates-table-container">
      <table className="ui celled table">
        <thead>
          <tr>
            <th>Worker</th>
            <th>Department</th>
            <th>Monday</th>
            <th>Tuesday</th>
            <th>Wednesday</th>
            <th>Thursday</th>
            <th>Friday</th>
            <th>Saturday</th>
            <th>Sunday</th>
          </tr>
        </thead>
        <tbody>
          {workers.map((worker) => (
            <tr key={worker.id}>
              <td>{`${worker.first_name} ${worker.last_name}`}</td>
              <td>{worker.department.name}</td>
              {["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"].map((day) => (
                <td key={day}>
                  {worker.schedules[day] ? (
                    <>
                      <input 
                        type="time" 
                        value={worker.schedules[day].shift_start} 
                        onChange={(event) => handleScheduleChange(event, worker.id, day, 'start')}
                      />
                      -
                      <input 
                        type="time" 
                        value={worker.schedules[day].shift_end} 
                        onChange={(event) => handleScheduleChange(event, worker.id, day, 'end')}
                      />
                    </>
                  ) : 'Off'}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AssociatesTable;
