import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { editAssociate, deleteWorker, updateAssociate } from '../features/workersSlice';
import Metric from './Metric';

function AssociateCard({ worker }) {
  const dispatch = useDispatch();
  const editableData = useSelector(state => 
    state.workers.editableAssociates[worker.id] ? {...worker, ...state.workers.editableAssociates[worker.id]} : worker
  );

  const handleChange = (e, metricId) => {
    const { name, value } = e.target;
    dispatch(editAssociate({ associateId: worker.id, field: name, value }));
  };

  const handleSave = () => {
    dispatch(updateAssociate(editableData));
  };

  function handleWorkerDelete() {
    fetch(`http://localhost:5555/associate_metrics/${worker.id}`, {
      method: 'DELETE',
    })
    .then(r => r.json())
    .then(data => {
      dispatch(deleteWorker(worker.id));
    });
  }

  const metricCards = worker.metrics && worker.metrics.map((metric, index) => (
    <Metric key={index} metric={metric} />
  ));

  return (
    <div className="ui card">
      <div className="content">
        <div className="header">{worker.first_name} {worker.last_name}</div>
        <div className="meta">{worker.job_class}</div>
      </div>
      <div className="content">
        <h4 className="ui sub header">Metrics</h4>
        <div className="ui small feed">
          {metricCards}
        </div>
      </div>
      <div className="extra content">
        <button onClick={handleWorkerDelete} className="ui button">Delete</button>
      </div> 
      <div>
        <input
          type="text"
          name="firstName"
          value={editableData.firstName}
          onChange={handleChange}
        />
        {/* Render other fields and metrics similarly */}
        <button onClick={handleSave}>Save Changes</button>
      </div>
    </div>
  );
}

export default AssociateCard;
