import React from 'react';
import { Formik, Form, Field, useField, ErrorMessage } from 'formik';
import { useDispatch } from 'react-redux';
import { addWorker } from '../features/workersSlice';
import * as Yup from 'yup';


// Custom Input component for Formik
const MyTextInput = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="error">{meta.error}</div>
      ) : null}
    </>
  );
};

// Custom Select component for Formik
const MySelect = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <select {...field} {...props} />
      {meta.touched && meta.error ? (
        <div className="error">{meta.error}</div>
      ) : null}
    </>
  );
};

// Validation Schema using Yup
const validationSchema = Yup.object({
  firstName: Yup.string().required('Required'),
  lastName: Yup.string().required('Required'),
  uptime: Yup.number().required('Required')
    .typeError('uptime must be a number')
    ,
  casesPerHour: Yup.number().required('Required'),
  palletsPerHour: Yup.number().required('Required'),
  attendance: Yup.number().required('Required')
    .typeError('attendance must be a number')
    .test(
      'is-float',
      'attendance must be a floating point number',
      value=>value != null && (value==0 || !Number.isInteger(value))
    ),
  jobClass_id: Yup.number().required('Required'),
});

function AssociateForm() {
  const dispatch = useDispatch();

  // Initial values for the form
  const initialValues = {
    firstName: '',
    lastName: '',
    uptime: '',
    casesPerHour: '',
    palletsPerHour: '',
    attendance: '',
    jobClass_id: '',
  };

  return (
    <div>
      <h3>Add an Associate!</h3>
      <Formik
        initialValues={initialValues}
        validationSchema={validationSchema}
        onSubmit={(values, { setSubmitting, resetForm }) => {
          // Extracting and handling metric-related properties as needed
          const { uptime, casesPerHour, palletsPerHour, attendance, ...otherData } = values;
          const metrics = { uptime, casesPerHour, palletsPerHour, attendance };

          // Dispatching the action with structured data
          dispatch(addWorker({ ...otherData, metrics }));

          // Reset Form
          resetForm();
          setSubmitting(false);
        }}
      >
        <Form>
          <MySelect label="Job Class" name="jobClass_id">
            <option value="">Select a Job Class</option>
            {/* Add options here */}
          </MySelect>
          <MyTextInput label="First Name" name="firstName" type="text" />
          <MyTextInput label="Last Name" name="lastName" type="text" />
          <MyTextInput label="Uptime" name="uptime" type="text" />

          {/* Conditionally render fields based on jobClass_id */}
          <Field name="jobClass_id">
            {({ field }) => {
              if (field.value === 'Selector') {
                return <MyTextInput label="Cases Per Hour" name="casesPerHour" type="text" />;
              } else if (field.value === 'Forklift' || field.value === 'Loader' || field.value === 'Receiver') {
                return <MyTextInput label="Pallets Per Hour" name="palletsPerHour" type="text" />;
              }
              return null;
            }}
          </Field>

          <MyTextInput label="Attendance" name="attendance" type="text" />

          <button type="submit">Submit</button>
        </Form>
      </Formik>
    </div>
  );
}

export default AssociateForm;
