import React from 'react';
import { useSelector } from 'react-redux';

function ExecutiveSummary() {
  const workers = useSelector(state => state.workers.workers);

  // Group workers by department
  const workersByDepartment = workers.reduce((acc, worker) => {
    const deptName = worker.department.name;
    if (!acc[deptName]) {
      acc[deptName] = [];
    }
    acc[deptName].push(worker);
    return acc;
  }, {});

  // Calculate average uptime by department
  const calculateAverageUptimeByDepartment = (departmentWorkers) => {
    if (departmentWorkers.length === 0) return 0;

    const totalUptime = departmentWorkers.reduce((total, worker) => {
      return total + (worker.metrics.Uptime || 0);
    }, 0);

    return totalUptime / departmentWorkers.length;
  };

  const averageUptimeByDepartment = Object.keys(workersByDepartment).map(department => ({
    department,
    averageUptime: calculateAverageUptimeByDepartment(workersByDepartment[department]),
  }));

  // Function to round numbers to two decimal places
  const formatNumber = (num) => Math.round(num * 100) / 100;

  return (
    <div className="header-container">
      <h1>Capacity Planner</h1>
      <div>Uptime by Department:</div>
      {averageUptimeByDepartment.map(({ department, averageUptime }) => (
        <div key={department}>
          {department}: {formatNumber(averageUptime)}% Uptime
        </div>
      ))}
    </div>
  );
}

export default ExecutiveSummary;
