import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { fetchAllocation } from '../features/allocationSlice';


const DepartmentForm = () => {
  const [departmentData, setDepartmentData] = useState({ departments: {}, total_heads: 0 });
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(fetchAllocation(departmentData));
  };

  // Add inputs for department data and a submit button
  // Update state with setDepartmentData based on user input

  return (
    <form onSubmit={handleSubmit}>
      {/* Form fields for department data */}
      <button type="submit">Calculate Allocation</button>
    </form>
  );
};

export default DepartmentForm;
