import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchWorkers } from '../features/workersSlice';

const AssociatesTable = () => {
  const dispatch = useDispatch();
  const { workers, status, error } = useSelector(state => state.workers);

  useEffect(() => {
    if (status === 'idle') {
      dispatch(fetchWorkers());
    }
  }, [status, dispatch]);

  const renderSchedule = (schedules) => {
    return Object.entries(schedules).map(([day, { shift_start, shift_end }]) => (
      <div key={day}>
        {day}: {shift_start} - {shift_end}
      </div>
    ));
  };

  if (status === 'loading') {
    return <div>Loading...</div>;
  } else if (status === 'failed') {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Department</th>
            <th>Schedule</th>
          </tr>
        </thead>
        <tbody>
          {workers.map((worker) => (
            <tr key={worker.id}>
              <td>{`${worker.first_name} ${worker.last_name}`}</td>
              <td>{worker.department.name}</td>
              <td>{renderSchedule(worker.schedules)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AssociatesTable;
