import React, { useState, useEffect } from 'react';
import { Button, Form, Table } from 'semantic-ui-react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSchedule } from '../features/scheduleBuildSlice';

function ScheduleForm() {
  const [inputValues, setInputValues] = useState({
    sunday: 12,
    monday: 13,
    tuesday: 14,
    wednesday: 15,
    thursday: 16,
    friday: 17,
    // ... other days
  });
  const dispatch = useDispatch();

  // This will hold the fetched schedule once available
  const schedule = useSelector((state) => state.scheduleBuilder.data);

  useEffect(() => {
    // Dispatch fetchSchedule action when the component mounts
    dispatch(fetchSchedule(inputValues));
  }, [dispatch, inputValues]);

  const handleSliderChange = (name, value) => {
    setInputValues({ ...inputValues, [name]: value });
  };

  const handleSubmit = () => {
    dispatch(fetchSchedule(inputValues));
  };

  // Define a function to render the schedule table
  function ScheduleTable() {
    if (!schedule) return <div>Loading schedule...</div>;

    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'];

    // Convert the schedule object into an array of arrays for easy table rendering
    const scheduleArray = days.map((day, index) => ({
      day,
      staff: schedule[`Shift for ${day}`]
    }));

    return (
      <Table celled>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Shift</Table.HeaderCell>
            {days.map(day => <Table.HeaderCell key={day}>{day}</Table.HeaderCell>)}
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {scheduleArray.map((shift, index) => (
            <Table.Row key={index}>
              <Table.Cell>{`Shift: ${shift.day}`}</Table.Cell>
              {days.map(day => (
                <Table.Cell key={day}>{day === shift.day ? shift.staff : 0}</Table.Cell>
              ))}
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    );
  }


  // Update the return statement to include ScheduleTable
  return (
    <div>
      <Form>
        {/* Form fields */}
      </Form>
      <Button onClick={handleSubmit} primary>
        Generate Schedule
      </Button>
      <ScheduleTable />
    </div>
  );
}

export default ScheduleForm;
