import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

// Define the async thunk for fetching the schedule
export const fetchSchedule = createAsyncThunk(
  'schedule/fetchSchedule',
  async (userInput, thunkAPI) => {
    try {
      // Make an API call to the endpoint that triggers the optimization
      const response = await axios.post('http://localhost:5555/optimize_schedule', userInput);
      return response.data; // The response data is the schedule returned from the Flask API
    } catch (error) {
      return thunkAPI.rejectWithValue(error.response.data);
    }
  }
);

// Create the schedule slice with initial state, reducers, and extra reducers
const scheduleBuildSlice = createSlice({
  name: 'schedule',
  initialState: {
    data: {}, // Start with an empty object to hold the schedule data
    status: 'idle',
    error: null
  },
  reducers: {
    // Reducers can be added here if needed
  },
  extraReducers: {
    [fetchSchedule.pending]: (state) => {
      state.status = 'loading';
    },
    [fetchSchedule.fulfilled]: (state, action) => {
      state.status = 'succeeded';
      state.data = action.payload; // Update the data on successful API response
    },
    [fetchSchedule.rejected]: (state, action) => {
      state.status = 'failed';
      state.error = action.error.message; // Set the error message on API failure
    }
  }
});

// Export the reducer
export default scheduleBuildSlice.reducer;
