import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSchedules } from '../features/schedulesSlice';

const ScheduleGrid = () => {
    const dispatch = useDispatch();
    const daysOfWeek = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    const schedules = useSelector((state) => state.schedules.data);
    const schedulesStatus = useSelector((state) => state.schedules.status);

    useEffect(() => {
        if (schedulesStatus === 'idle') {
            dispatch(fetchSchedules());
        }
    }, [schedulesStatus, dispatch]);

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th>Associate Name</th>
                        {daysOfWeek.map(day => (
                            <th key={day}>{day}</th>
                        ))}
                    </tr>
                </thead>
                <tbody>
                    {schedules.map(associate => (
                        <tr key={associate.associate_id}>
                            <td>{`${associate.first_name} ${associate.last_name}`}</td>
                            {daysOfWeek.map(day => (
                                <td key={day}>{associate.schedule.includes(day) ? 'Scheduled' : 'Off'}</td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default ScheduleGrid;
