import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchWorkers, updateAssociate } from '../features/workersSlice';

const AssociatesTable = () => {
  const dispatch = useDispatch();
  const { workers, status } = useSelector((state) => state.workers);
  console.log()



  const handleUpdate = (workerId, updatedSchedule) => {
    dispatch(updateAssociate({ associateId: workerId, schedules: updatedSchedule }));
  };

  return (
    <div className="associates-table-container">
      <table className="ui celled table">
        <thead>
          <tr>
            <th>Worker</th>
            <th>Department</th>
            {/* Add other headers as needed */}
          </tr>
        </thead>
        <tbody>
          {workers.map((worker) => (
            <tr key={worker.id}>
              <td>{`${worker.first_name} ${worker.last_name}`}</td>
              <td>{worker.department.name}</td>
              {/* Implement editing fields for each worker */}
              {/* This is a simplification. You would need to add inputs for each day of the week
                 and handle their changes similarly. */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AssociatesTable;
