import React from 'react';
import { useSelector } from 'react-redux';

function ExecutiveSummary() {
  const workers = useSelector(state => state.workers.workers);

  let headCount = workers.length
  const calculateAverageCPH = () => {
    if (workers.length === 0) return 0;

    // Calculate total uptime by summing up uptime of all workers
    const totalCPH = workers.reduce((total, worker) => {
      // Check if worker has cases per hour
      if (worker.metrics && worker.metrics.CasesPerHour !== undefined) {
          return total + worker.metrics.CasesPerHour;
      } else {
          // If cases per hour is missing, consider it as 0
          return total;
      }
  }, 0);

    // Return average uptime
    return totalCPH / workers.length;
  };

  const calculateAverageUptime = () => {
    if (workers.length === 0) return 0;

    // Calculate total uptime by summing up uptime of all workers
    const totalUptime = workers.reduce((total, worker) => {
      return total + worker.metrics.Uptime;
    }, 0);

    // Return average uptime
    return totalUptime / workers.length;
  };

  const calculateAverageAttendance = () => {
    if (workers.length === 0) return 0;

    // Calculate total uptime by summing up uptime of all workers
    const totalAttendance = workers.reduce((total, worker) => {
      return total + worker.metrics.Attendance;
    }, 0);

    // Return average uptime
    return totalAttendance / workers.length;
  };
  const averageUptime = calculateAverageUptime();
  const averageAttendance = calculateAverageAttendance();
  console.log('averageAttendance',averageAttendance)
  const averageCPH = calculateAverageCPH();
  console.log('averageCPH',averageCPH)
  let totalCapacity = headCount*averageAttendance*averageCPH * averageUptime
  console.log('total cap',totalCapacity)

  // Function to round numbers to two decimal places
  const formatNumber = (num) => Math.round(num * 100) / 100;

  return (
    <div className="header-container">
      <h1>Capacity Planner</h1>
      <table className="ui definition table">
        <thead>
          <tr>
            <th></th>
            <th>Metrics</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>Total Capacity</td>
            <td>{formatNumber(totalCapacity)} Cases</td>
            <td>(average associate cases per hour * average uptime percentage) * 8 hour day</td>
          </tr>
          <tr>
            <td>Total Headcount</td>
            <td>{headCount} Workers</td>
            <td>Sums of total employees scheduled</td>
          </tr>
          <tr>
            <td>Average Attendance</td>
            <td>{formatNumber(averageAttendance)}% Attendance</td>
            <td>Average attendance of all employees</td>
          </tr>
          <tr>
            <td>Average Cases Per Hour</td>
            <td>{formatNumber(averageCPH)} Cases Per Hour</td>
            <td>Average cases per hour of all employees</td>
          </tr>
          <tr>
            <td>Average Uptime</td>
            <td>{formatNumber(averageUptime)}% Uptime</td>
            <td>Average uptime of all employees</td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

export default ExecutiveSummary;
