import React, { useState } from 'react';
import { Button, Form, Slider } from 'semantic-ui-react';
import { useDispatch } from 'react-redux';
import { fetchSchedule } from './scheduleSlice';

function ScheduleForm() {
  const [inputValues, setInputValues] = useState({
    sunday: 12, // default values
    monday: 13,
    // ... other days
  });
  const dispatch = useDispatch();

  const handleSliderChange = (name, value) => {
    setInputValues({ ...inputValues, [name]: value });
  };

  const handleSubmit = () => {
    dispatch(fetchSchedule(inputValues));
  };

  return (
    <Form>
      <Form.Field>
        <label>Sunday Needs (heads)</label>
        <input
          type='range'
          min={1}
          max={100}
          value={inputValues.sunday}
          onChange={(e) => handleSliderChange('sunday', parseInt(e.target.value))}
        />
      </Form.Field>
      {/* Repeat for other days */}
      <Button onClick={handleSubmit} primary>Generate Schedule</Button>
    </Form>
  );
}

export default ScheduleForm;
