import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { fetchAllocation } from '../features/allocationSlice';

const DepartmentForm = () => {
  const [departmentData, setDepartmentData] = useState({ departments: {}, total_heads: 0 });
  const dispatch = useDispatch();

  // Update this handler to manage changes in department cases and cases per hour
  const handleDepartmentChange = (deptName, totalCases, casesPerHour) => {
    setDepartmentData(prevState => ({
      ...prevState,
      departments: {
        ...prevState.departments,
        [deptName]: { total_cases: totalCases, cases_per_hour: casesPerHour }
      }
    }));
  };

  // Update this handler to manage changes in total heads
  const handleHeadsChange = (totalHeads) => {
    setDepartmentData(prevState => ({
      ...prevState,
      total_heads: totalHeads
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(fetchAllocation(departmentData));
  };

  // Example with one department, replicate the inputs for each department you need
  return (
    <form onSubmit={handleSubmit}>
      {/* Assuming you have multiple departments, you would map over them here to generate these inputs */}
      <div>
        <label>Department Name:</label>
        <input
          type="text"
          onChange={(e) => handleDepartmentChange(e.target.value, departmentData.departments[e.target.value]?.total_cases, departmentData.departments[e.target.value]?.cases_per_hour)}
        />
      </div>
      <div>
        <label>Total Cases:</label>
        <input
          type="number"
          onChange={(e) => handleDepartmentChange(e.target.name, Number(e.target.value), departmentData.departments[e.target.name]?.cases_per_hour)}
          name="deptName" // Replace with actual department name
        />
      </div>
      <div>
        <label>Cases Per Hour:</label>
        <input
          type="number"
          onChange={(e) => handleDepartmentChange(e.target.name, departmentData.departments[e.target.name]?.total_cases, Number(e.target.value))}
          name="deptName" // Replace with actual department name
        />
      </div>
      <div>
        <label>Total Heads:</label>
        <input
          type="number"
          value={departmentData.total_heads}
          onChange={(e) => handleHeadsChange(Number(e.target.value))}
        />
      </div>
      <button type="submit">Calculate Allocation</button>
    </form>
  );
};

export default DepartmentForm;
