import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import MasterOperatingPlan from './MasterOperatingPlan';
import About from './About';
import ExecutiveSummary from './ExecutiveSummary';
import AssociateForm from './AssociateForm';
import AllocationSummary from './AllocationSummary';
import ScheduleForm from './ScheduleForm';
import AssociatesTable from './AssociatesTable';
import { fetchWorkers } from '../features/workersSlice';
import '../index.css';

function App() {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchWorkers());
  }, [dispatch]);

  return (
    <Router>
      <div className="flex flex-col h-screen">
        {/* Navigation Bar */}
        <header className="navbar bg-base-100">
          <div className="flex-grow">
            <div className="flex justify-between items-center w-full px-4">
              <Link to="/"><button className="btn btn-primary btn-lg">Roster</button></Link>
              <Link to="/employeeform"><button className="btn btn-secondary btn-lg">Add Associate</button></Link>
              <Link to="/executivesummary"><button className="btn btn-accent btn-lg">Executive Summary</button></Link>
              {/* Add more navigation buttons as needed */}
            </div>
          </div>
        </header>

        {/* Hero Section */}
        <HeroSection />

        {/* Footer */}
        <footer className="footer bg-base-300 text-base-content">
          <div className="items-center grid-flow-col">
            <p>© 2024 Company Name</p>
          </div>
        </footer>

        {/* Routes */}
        <Routes>
          <Route path="/executivesummary" element={<ExecutiveSummary/>} />
          <Route path="/about" element={<About/>} />
          <Route path="/employeeform" element={<AssociateForm/>} />
          <Route path="/" element={<MasterOperatingPlan/>} />
          <Route path="/associatetable" element={<AssociatesTable/>} />
          <Route path="/schedulebuilder" element={<ScheduleForm/>} />
          <Route path="/allocationsummary" element={<AllocationSummary/>} />
          {/* Add more routes as needed */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;

// HeroSection.js
function HeroSection() {
  // State to control the visibility of the modal
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="hero min-h-screen bg-base-200">
      <div className="hero-content text-center">
        <div className="max-w-md">
          <h1 className="text-5xl font-bold">Welcome to Your Application</h1>
          <p className="py-6">Manage your operations effectively.</p>
          <button className="btn btn-primary btn-lg" onClick={openModal}>Get Started</button>
        </div>
      </div>

      {/* Modal for instructions */}
      {isModalOpen && (
        <div className={`modal ${isModalOpen ? 'modal-open' : ''}`}>
          <div className="modal-box relative">
            <label htmlFor="my-modal" className="btn btn-sm btn-circle absolute right-2 top-2" onClick={closeModal}>✕</label>
            <h3 className="text-lg font-bold">Instructions</h3>
            <p className="py-4">Here are the steps to use this system efficiently:</p>
            {/* Add instructions or content as needed */}
            <div className="modal-action">
              <button className="btn btn-primary" onClick={closeModal}>Got it!</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
