import React, { useState } from 'react';
import { Form } from 'semantic-ui-react';
import { useDispatch } from 'react-redux';
import { addWorker } from '../features/workersSlice';

function AssociateForm() {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    uptime: '',
    casesPerHour: '',
    palletsPerHour: '',
    attendance: '',
    jobClass_id: '',
  });

  const dispatch = useDispatch();

  function handleChange(event, { name, value }) {
    setFormData({
      ...formData,
      [name]: value,
    });
  }

  function handleSubmit(event) {
    event.preventDefault();

    // Extracting metric-related properties
    const { uptime, casesPerHour, palletsPerHour, attendance, ...otherData } = formData;

    // Constructing a metrics object
    const metrics = {
      Uptime,
      CasesPerHour,
      PalletsPerHour,
      attendance
    };

    // Removing undefined or null metric values
    Object.keys(metrics).forEach(key => {
      if (metrics[key] == null || metrics[key] === '') {
        delete metrics[key];
      }
    });

    // Dispatching the action with structured data
    console.log({ ...otherData, metrics });
    dispatch(addWorker({ ...otherData, metrics })); // Include other non-metric data along with the structured metrics

    // Reset formData after submission
    setFormData({
      firstName: '',
      lastName: '',
      uptime: '',
      casesPerHour: '',
      palletsPerHour: '',
      attendance: '',
      jobClass_id: '',
    });
  }

  // Function to render job-specific metrics
  function renderJobSpecificFields() {
    switch (formData.jobClass_id) {
      case 'Selector':
        return (
          <Form.Input
            fluid
            label="Cases Per Hour"
            placeholder="Cases Per Hour"
            name="casesPerHour"
            value={formData.casesPerHour}
            onChange={handleChange}
          />
        );
      case 'Forklift':
        return (
          <Form.Input
            fluid
            label="Pallets Per Hour"
            placeholder="Pallets Per Hour"
            name="palletsPerHour"
            value={formData.palletsPerHour}
            onChange={handleChange}
          />
        );
      case 'Loader':
        return (
          <Form.Input
            fluid
            label="Pallets Per Hour"
            placeholder="Pallets Per Hour"
            name="palletsPerHour"
            value={formData.palletsPerHour}
            onChange={handleChange}
          />
        );
      case 'Receiver':
        return (
          <Form.Input
            fluid
            label="Pallets Per Hour"
            placeholder="Pallets Per Hour"
            name="palletsPerHour"
            value={formData.palletsPerHour}
            onChange={handleChange}
          />
        );
      default:
        return null;
    }
  }

  return (
    <div>
      <h3>Add an Associate!</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="field" widths="equal">
          <Form.Select
            fluid
            label="Job Class"
            options={[
              { key: 'selector', value: 1, text: 'Selector' },
              { key: 'forklift', value: 2, text: 'Forklift' },
              { key: 'receiver', value: 4, text: 'Receiver' },
              { key: 'loader', value: 3, text: 'Loader' },
              // ... Add other job class options here ...
            ]}
            placeholder="Select a Job Class"
            name="jobClass_id"
            value={formData.jobClass_id}
            onChange={handleChange}
          />
          <Form.Input
            fluid
            label="First Name"
            placeholder="First Name"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
          />
          <Form.Input
            fluid
            label="Last Name"
            placeholder="Last Name"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
          />
          <Form.Input
            fluid
            label="Uptime"
            placeholder="Uptime"
            name="uptime"
            value={formData.uptime}
            onChange={handleChange}
          />
          {renderJobSpecificFields()}
          <Form.Input
            fluid
            label="Attendance"
            placeholder="Attendance"
            name="attendance"
            value={formData.attendance}
            onChange={handleChange}
          />
        </Form.Group>
        <Form.Button>Submit</Form.Button>
      </Form>
    </div>
  );
}

export default AssociateForm;
