import React from 'react';
import { useSelector } from 'react-redux';

function ExecutiveSummary() {
  const workers = useSelector(state => state.workers.workers);

  // Group workers by department
  const workersByDepartment = workers.reduce((acc, worker) => {
    const deptName = worker.department.name;
    if (!acc[deptName]) {
      acc[deptName] = [];
    }
    acc[deptName].push(worker);
    return acc;
  }, {});

  // Calculate average uptime by department
  const calculateAverageUptimeByDepartment = (departmentWorkers) => {
    if (departmentWorkers.length === 0) return 0;

    const totalUptime = departmentWorkers.reduce((total, worker) => {
      return total + (worker.metrics.Uptime || 0);
    }, 0);

    return totalUptime / departmentWorkers.length;
  };

  const averageUptimeByDepartment = Object.keys(workersByDepartment).map(department => ({
    department,
    averageUptime: calculateAverageUptimeByDepartment(workersByDepartment[department]),
  }));

  const calculateAverageAttendanceByDepartment = (departmentWorkers) => {
    if (departmentWorkers.length === 0) return 0;

    const totalAttendance = departmentWorkers.reduce((total, worker) => {
      return total + (worker.metrics.Attendance || 0);
    }, 0);

    return totalAttendance / departmentWorkers.length;
  };

  const averageAttendanceByDepartment = Object.keys(workersByDepartment).map(department => ({
    department,
    averageAttendance: calculateAverageAttendanceByDepartment(workersByDepartment[department]),
  }));

  // Function to round numbers to two decimal places
  const formatNumber = (num) => Math.round(num * 100) / 100;

  return (
    <div className="header-container">
      <h1>Capacity Planner</h1>
      <table className="ui definition table">
        <thead>
          <tr>
            <th></th>
            <th>Metrics</th>
            <th>Description</th>
          </tr>
        </thead>
        <tbody>
          {/* Existing Metrics Rows Here */}
        </tbody>
      </table>

      {/* New Table for Uptime by Department */}
      <h2>Average Uptime by Department</h2>
      <table className="ui celled table">
        <thead>
          <tr>
            <th>Department</th>
            <th>Average Uptime (%)</th>
          </tr>
        </thead>
        <tbody>
          {averageUptimeByDepartment.map(({ department, averageUptime }) => (
            <tr key={department}>
              <td>{department}</td>
              <td>{formatNumber(averageUptime)}%</td>
            </tr>
          ))}
        </tbody>
      </table>
      {/* New Table for Attendance by Department */}
      <h2>Average Attendance by Department</h2>
      <table className="ui celled table">
        <thead>
          <tr>
            <th>Department</th>
            <th>Average Attendance (%)</th>
          </tr>
        </thead>
        <tbody>
          {averageAttendanceByDepartment.map(({ department, averageAttendance }) => (
            <tr key={department}>
              <td>{department}</td>
              <td>{formatNumber(averageAttendance)}%</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ExecutiveSummary;
