import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSchedule } from '../features/scheduleSlice';

function ScheduleForm() {
  const [dailyDemand, setDailyDemand] = useState([0, 0, 0, 0, 0, 0, 0]);
  const dispatch = useDispatch();
  const scheduleData = useSelector((state) => state.schedule.scheduleData); // Updated selector

  const error = useSelector((state) => state.schedule.error);

  const handleInputChange = (e, index) => {
    const newDailyDemand = [...dailyDemand];
    newDailyDemand[index] = Number(e.target.value);
    setDailyDemand(newDailyDemand);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting daily demand:', dailyDemand);
    try {
      await dispatch(fetchSchedule(dailyDemand));
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Schedule Generator</h1>
      {error && <p>Error: {error}</p>}
      <form onSubmit={handleSubmit}>
        {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map((day, index) => (
          <div key={day}>
            <label>
              {day}:
              <input
                type="number"
                value={dailyDemand[index]}
                onChange={(e) => handleInputChange(e, index)}
              />
            </label>
          </div>
        ))}
        <button type="submit">Generate Schedule</button>
      </form>

      {scheduleData && scheduleData.length > 0 && (
        <table>
          <thead>
            <tr>
              <th>Shift</th>
              {Object.keys(scheduleData[0]).map((day) => (
                <th key={day}>{day}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {scheduleData.map((shiftData, shiftIndex) => (
              <tr key={shiftIndex}>
                <td>Shift {shiftIndex + 1}</td>
                {Object.values(shiftData).map((count, dayIndex) => (
                  <td key={dayIndex}>{count}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default ScheduleForm;
