// Example slider and submit button
import React, { useState } from 'react';
import Slider from '@material-ui/core/Slider';
import Button from '@material-ui/core/Button';
import { useDispatch } from 'react-redux';
import { fetchSchedule } from '../features/scheduleBuildSlice';

function ScheduleForm() {
  const [inputValues, setInputValues] = useState({ /* initial values */ });
  const dispatch = useDispatch();

  const handleSliderChange = (name) => (event, newValue) => {
    setInputValues({ ...inputValues, [name]: newValue });
  };

  const handleSubmit = () => {
    dispatch(fetchSchedule(inputValues));
  };

  return (
    <div>
      {/* Sliders for each day */}
      <Slider name="sunday" value={inputValues.sunday} onChange={handleSliderChange('sunday')} />
      {/* Add other sliders similarly */}
      <Button onClick={handleSubmit}>Generate Schedule</Button>
    </div>
  );
}

export default ScheduleForm;
