import React from 'react';
import { useSelector } from 'react-redux';

function ExecutiveSummary() {
  const workers = useSelector(state => state.workers.workers);

  // Group workers by department and then by job class
  const workersByDepartmentAndJobClass = workers.reduce((acc, worker) => {
    const deptName = worker.department.name;
    const jobClassName = worker.jobclass.name;

    if (!acc[deptName]) {
      acc[deptName] = {};
    }
    if (!acc[deptName][jobClassName]) {
      acc[deptName][jobClassName] = [];
    }

    acc[deptName][jobClassName].push(worker);
    return acc;
  }, {});

  // Calculate average uptime and attendance
  const calculateAverages = (workers) => {
    if (workers.length === 0) return { averageUptime: 0, averageAttendance: 0 };

    const totals = workers.reduce((totals, worker) => {
      totals.uptime += worker.metrics.Uptime || 0;
      totals.attendance += worker.metrics.Attendance || 0;
      return totals;
    }, { uptime: 0, attendance: 0 });

    return {
      averageUptime: totals.uptime / workers.length,
      averageAttendance: totals.attendance / workers.length
    };
  };

  const getAveragesByDepartmentAndJobClass = () => {
    return Object.entries(workersByDepartmentAndJobClass).map(([department, jobClasses]) => ({
      department,
      jobClasses: Object.entries(jobClasses).map(([jobClass, workers]) => {
        const { averageUptime, averageAttendance } = calculateAverages(workers);
        return {
          jobClass,
          averageUptime,
          averageAttendance
        };
      })
    }));
  };

  const averagesData = getAveragesByDepartmentAndJobClass();

  // Function to round numbers to two decimal places
  const formatNumber = (num) => Math.round(num * 100) / 100;

  return (
    <div className="header-container">
      <h1>Capacity Planner</h1>

      {/* New Section for Uptime and Attendance by Department and Job Class */}
      <h2>Averages by Department and Job Class</h2>
      {averagesData.map(({ department, jobClasses }) => (
        <div key={department}>
          <h3>{department}</h3>
          <table className="ui celled table">
            <thead>
              <tr>
                <th>Job Class</th>
                <th>Average Uptime (%)</th>
                <th>Average Attendance (%)</th>
              </tr>
            </thead>
            <tbody>
              {jobClasses.map(({ jobClass, averageUptime, averageAttendance }) => (
                <tr key={jobClass}>
                  <td>{jobClass}</td>
                  <td>{formatNumber(averageUptime)}%</td>
                  <td>{formatNumber(averageAttendance)}%</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      ))}
    </div>
  );
}

export default ExecutiveSummary;
