import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import './AssociatesTable.css';

const AssociatesTable = () => {
  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];
  const workers = useSelector((state) => state.workers.workers); // Adjusted to the correct slice
  const [currentAssociate, setCurrentAssociate] = useState(null);

  const openModal = (associate) => {
    setCurrentAssociate(associate);
  };

  const closeModal = () => {
    setCurrentAssociate(null);
  };

  const handleCheckboxChange = (day) => {
    const includesDay = currentAssociate?.working_days?.includes(day);
    if (includesDay) {
      setCurrentAssociate({
        ...currentAssociate,
        working_days: currentAssociate.working_days.filter(d => d !== day),
      });
    } else {
      setCurrentAssociate({
        ...currentAssociate,
        working_days: [...(currentAssociate.working_days || []), day],
      });
    }
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    // Implement your submit logic here, for example, update the worker data
    console.log('Submitting form for:', currentAssociate);
    // After form submission, you may want to close the form
    closeModal();
  };

  return (
    <div className="associates-table-container">
      <table className="ui definition table">
        <thead>
          <tr>
            <th>Name</th>
            {daysOfWeek.map(day => <th key={day}>{day}</th>)}
          </tr>
        </thead>
        <tbody>
          {workers.map(worker => (
            <tr key={worker.id} onClick={() => openModal(worker)} style={{ cursor: 'pointer' }}>
              <td>{`${worker.first_name} ${worker.last_name}`}</td>
              {daysOfWeek.map(day => (
                <td key={day}>X</td> // Replace 'X' with your logic to display work days
              ))}
            </tr>
          ))}
        </tbody>
      </table>
      {currentAssociate && (
        <div className="associate-form">
          <h2>Editing schedule for {currentAssociate.first_name} {currentAssociate.last_name}</h2>
          <form onSubmit={handleFormSubmit}>
            {daysOfWeek.map(day => (
              <div key={day}>
                <label>
                  <input
                    type="checkbox"
                    checked={currentAssociate.working_days?.includes(day)}
                    onChange={() => handleCheckboxChange(day)}
                  />
                  {day}
                </label>
              </div>
            ))}
            <button type="submit">Save</button>
            <button type="button" onClick={closeModal}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
};

export default AssociatesTable;
