import React, { useState } from 'react';
import { Formik, Form, useField } from 'formik';
import { useDispatch } from 'react-redux';
import { addWorker } from '../features/workersSlice';
import * as Yup from 'yup';

// Define the MySelect component
const MySelect = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <select {...field} {...props} />
      {meta.touched && meta.error ? <div className="error">{meta.error}</div> : null}
    </>
  );
};

// Define the jobClasses array
const jobClasses = [
  { id: 1, name: 'Putaway Forklift' },
  { id: 2, name: 'Selector' },
  { id: 3, name: 'Loader' },
  { id: 4, name: 'Receiver' },
  { id: 5, name: 'Letdown Forklift' },
  // ... other job classes ...
];

// Custom Input component for Formik
const MyTextInput = ({ label, ...props }) => {
  const [field] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input {...field} {...props} />
    </>
  );
};

function AssociateForm() {
  const dispatch = useDispatch();
  const [selectedJobClass, setSelectedJobClass] = useState('');
  const validationSchema = Yup.object({
    firstName: Yup.string().required('First name is required'),
    lastName: Yup.string().required('Last name is required'),
    jobClass_id: Yup.number().required('Job class is required'), // Changed to number if IDs are numeric
    uptime: Yup.string().required('Uptime is required'),
    casesPerHour: Yup.string().when('jobClass_id', {
      is: 2,
      then: (schema) => schema.required('Cases per hour is required'),
    }),
    palletsPerHour: Yup.string().when('JobClass_id', {
      is: 1 || 3 || 4 || 5,
      then: (schema) => schema.required('Pallets per hour is required'),
    }),
    attendance: Yup.string().required('Attendance is required'),
    department: Yup.string().required('Department is required'), // New department field validation
    hireDate: Yup.date().required('Hire date is required'), // New hire date field validation
  });

  const handleJobClassChange = (setFieldValue, value) => {
    setFieldValue('jobClass_id', value);
    setSelectedJobClass(value);
  };

  // Initial values for the form
  const initialValues = {
    firstName: '',
    lastName: '',
    jobClass_id: '',
    uptime: '',
    casesPerHour: '',
    palletsPerHour: '',
    attendance: '',
    department: '', // New department field
    hireDate: '', // New hire date field
  };

  // onSubmit function with Redux dispatch
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    // Prepare the metrics object
    let metrics = [];
    const metricNames = {
      uptime: 'Uptime',
      casesPerHour: 'Cases Per Hour',
      palletsPerHour: 'Pallets Per Hour',
      attendance: 'Attendance',
    };

    Object.keys(metricNames).forEach((key) => {
      if (values[key]) {
        metrics.push({
          name: metricNames[key],
          value: values[key],
        });
      }
    });

    // Create the payload to send to the server
    const payload = {
      firstName: values.firstName,
      lastName: values.lastName,
      jobClass_id: parseInt(values.jobClass_id, 10), // Convert jobClass_id to an integer
      metrics: metrics,
      department: values.department, // Include department in the payload
      hireDate: values.hireDate, // Include hire date in the payload
    };

    // Dispatching the action with form data
    dispatch(addWorker(payload))
      .unwrap()
      .then((addedWorker) => {
        resetForm();
      })
      .catch((error) => {
        console.error('Form submission error:', error);
      })
      .finally(() => {
        setSubmitting(false);
      });
  };

  return (
    <div>
      <h3>Add an Associate!</h3>
      <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
        {({ isSubmitting, setFieldValue, values, errors, handleChange }) => (
          <Form>
            <MySelect label="Job Class" name="jobClass_id" onChange={(e) => handleJobClassChange(setFieldValue, e.target.value)}>
              <option value="">Select a Job Class</option>
              {jobClasses.map((jobClass) => (
                <option key={jobClass.id} value={jobClass.id}>
                  {jobClass.name}
                </option>
              ))}
            </MySelect>

            <MyTextInput label="Department" name="department" type="text" onChange={handleChange} />
            <MyTextInput label="Hire Date" name="hireDate" type="date" onChange={handleChange} />

            {selectedJobClass === '1' && (
              <>
                {/* Render input fields for Putaway Forklift job class */}
              </>
            )}
            {selectedJobClass === '2' && (
              <>
                {/* Render input fields for Selector job class */}
              </>
            )}
            {/* Render input fields for other job classes */}
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default AssociateForm;
