import React, { useState, useEffect } from 'react';
import { Button, Form, Table } from 'semantic-ui-react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSchedule } from '../features/scheduleBuildSlice';

function ScheduleForm() {
  const [inputValues, setInputValues] = useState({
    Monday: 0,
    Tuesday: 0,
    Wednesday: 0,
    Thursday: 0,
    Friday: 0,
    Saturday: 0,
    Sunday: 0
  });
  const dispatch = useDispatch();
  const scheduleData = useSelector((state) => state.schedule.data);

  useEffect(() => {
    dispatch(fetchSchedule(inputValues));
  }, [dispatch]);

  const handleSliderChange = (name, value) => {
    setInputValues({ ...inputValues, [name]: value });
  };

  const handleSubmit = () => {
    dispatch(fetchSchedule(inputValues));
  };

  function ScheduleTable({ schedule }) {
    const days = Object.keys(inputValues);

    return (
      <Table celled>
        <Table.Header>
          <Table.Row>
            <Table.HeaderCell>Day</Table.HeaderCell>
            <Table.HeaderCell>Required Staff</Table.HeaderCell>
          </Table.Row>
        </Table.Header>
        <Table.Body>
          {days.map((day) => (
            <Table.Row key={day}>
              <Table.Cell>{day}</Table.Cell>
              <Table.Cell>{schedule[day]}</Table.Cell>
            </Table.Row>
          ))}
        </Table.Body>
      </Table>
    );
  }

  return (
    <div>
      <Form>
        {Object.keys(inputValues).map((day) => (
          <Form.Field key={day}>
            <label>{day} Needs (heads)</label>
            <input
              type='range'
              min={0}
              max={100}
              value={inputValues[day]}
              onChange={(e) => handleSliderChange(day, parseInt(e.target.value))}
            />
            <div>Current: {inputValues[day]}</div>
          </Form.Field>
        ))}
      </Form>
      <Button onClick={handleSubmit} primary>
        Generate Schedule
      </Button>
      {scheduleData && <ScheduleTable schedule={scheduleData} />}
    </div>
  );
}

export default ScheduleForm;
