import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { deleteWorker, updateAssociate } from '../features/workersSlice';
import Metric from './Metric';
import { useFormik } from 'formik';
import * as Yup from 'yup';

function AssociateCard({ worker }) {

  const validationSchema = Yup.object({
    firstName: Yup.string().required('Required'),
    lastName: Yup.string().required('Required'),
    metrics: Yup.array().of(
      Yup.object({
        id: Yup.string().required('ID is required'),
        value: Yup.number().required('Value is required').positive('Value must be positive')
      })
    )
  });


  const dispatch = useDispatch();

  const metricsArray = Object.entries(worker.metrics).map(([key, value]) => ({
    id: key,
    value: value
  }));

  const formik = useFormik({
    initialValues: {
      firstName: worker.first_name,
      lastName: worker.last_name,
      metrics: metricsArray,
    },
    validationSchema: validationSchema,
    onSubmit: values => {
      const updatedData = {
        associateId: worker.id,
        firstName: values.firstName,
        lastName: values.lastName,
        metrics: values.metrics,
      };

      dispatch(updateAssociate(updatedData))
        .unwrap()
        .then(response => {
          // Assuming you want to do something on success
        })
        .catch(error => {
          console.error('Error updating associate:', error);
        });
    },
  });


  const [editMode, setEditMode] = useState(false);
  const [firstName, setFirstName] = useState(worker.first_name);
  const [lastName, setLastName] = useState(worker.last_name);
  const [metrics, setMetrics] = useState(metricsArray)


  function handleWorkerDelete() {
      dispatch(deleteWorker(worker.id));
  }

  function handleUpdate() {
    const updatedData = {
      associateId: worker.id,
      firstName: firstName,
      lastName: lastName,
      metrics: metrics 
    };


    dispatch(updateAssociate(updatedData))
      .unwrap()
      .then(response => {
        setEditMode(false);
      })
      .catch(error => {
        console.error('Error updating associate:', error);
      });
  }


  function handleMetricChange(metricId, newValue) {
    // Map over the metrics and update the value where the id matches
    const newMetrics = metrics.map(metric => {
      if (metric.id === metricId) {
        return { ...metric, value: parseInt(newValue, 10) };
      }
      return metric;
    });
    setMetrics(newMetrics);
  }



  const metricInputs = metrics.map((metric, index) => (
    <input
      key={index}
      type="number"
      value={metric.value}
      onChange={e => handleMetricChange(metric.id, e.target.value)}
    />
  ));


  return (
    <div className="ui card">
      <form onSubmit={formik.handleSubmit} className="ui form">
        <div className="field">
          <label>First Name</label>
          <input
            type="text"
            {...formik.getFieldProps('firstName')}
          />
          {formik.touched.firstName && formik.errors.firstName ? (
            <div>{formik.errors.firstName}</div>
          ) : null}
        </div>
        <div className="field">
          <label>Last Name</label>
          <input
            type="text"
            {...formik.getFieldProps('lastName')}
          />
          {formik.touched.lastName && formik.errors.lastName ? (
            <div>{formik.errors.lastName}</div>
          ) : null}
        </div>
        <div className="field">
          <label>Metrics</label>
          {formik.values.metrics.map((metric, index) => (
            <div key={metric.id}>
              <label htmlFor={`metrics.${index}.value`}>{`Metric ${index + 1}`}</label>
              <input
                type="number"
                {...formik.getFieldProps(`metrics.${index}.value`)}
              />
              {formik.errors.metrics && formik.errors.metrics[index] && formik.errors.metrics[index].value ? (
                <div>{formik.errors.metrics[index].value}</div>
              ) : null}
            </div>
          ))}
        </div>
        <button type="submit" className="ui button primary">Save</button>
        <button type="button" onClick={() => formik.resetForm()} className="ui button">
          Cancel
        </button>
      </form>
    </div>
  );
}



