import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { fetchSchedule } from '../features/';

function ScheduleFormTwo() {
  const [staffNeeds, setStaffNeeds] = useState({ sunday: 0, monday: 0, tuesday: 0, wednesday: 0, thursday: 0, friday: 0, saturday: 0 });
  const dispatch = useDispatch();

  const handleInputChange = (e) => {
    setStaffNeeds({
      ...staffNeeds,
      [e.target.name]: Number(e.target.value),
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(fetchSchedule(staffNeeds));
  };

  return (
    <form onSubmit={handleSubmit}>
      {Object.keys(staffNeeds).map((day) => (
        <div key={day}>
          <label>
            {day.charAt(0).toUpperCase() + day.slice(1)}: 
            <input
              type="number"
              name={day}
              value={staffNeeds[day]}
              onChange={handleInputChange}
            />
          </label>
        </div>
      ))}
      <button type="submit">Generate Schedule</button>
    </form>
  );
}

export default ScheduleFormTwo;
