import React, { useState } from 'react';
import { Formik, Form, useField } from 'formik';
import { useDispatch } from 'react-redux';
import { addWorker } from '../features/workersSlice';
import { useSelector } from 'react-redux';
import * as Yup from 'yup';

// Define the MySelect component
const MySelect = ({ label, options, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <select {...field} {...props}>
        {options.map((option) => (
          <option key={option.value} value={option.value}>
            {option.label}
          </option>
        ))}
      </select>
      {meta.touched && meta.error ? <div className="error">{meta.error}</div> : null}
    </>
  );
};

// Custom Input component for Formik
const MyTextInput = ({ label, ...props }) => {
  const [field] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input {...field} {...props} />
    </>
  );
};

function AssociateForm() {
  const dispatch = useDispatch();
  const [selectedJobClass, setSelectedJobClass] = useState('');
  const validationSchema = Yup.object({
    firstName: Yup.string().required('First name is required'),
    lastName: Yup.string().required('Last name is required'),
    jobClass_id: Yup.number().required('Job class is required'),
    uptime: Yup.string().required('Uptime is required'),
    casesPerHour: Yup.string().when('jobClass_id', {
      is: 2,
      then: (schema) => schema.required('Cases per hour is required'),
    }),
    palletsPerHour: Yup.string().when('JobClass_id', {
      is: 1 || 3 || 4 || 5,
      then: (schema) => schema.required('Pallets per hour is required'),
    }),
    attendance: Yup.string().required('Attendance is required'),
    department: Yup.string().required('Department is required'),
    hireDate: Yup.date().required('Hire date is required'),
  });

  const handleJobClassChange = (setFieldValue, value) => {
    setFieldValue('jobClass_id', value);
    setSelectedJobClass(value);
  };

  // Get the list of workers from the Redux store
  const workers = useSelector(state => state.workers.workers);

  // Extract unique department names from workers
  const uniqueDepartments = [...new Set(workers.map(worker => worker.department.name))];

  // Initial values for the form
  const initialValues = {
    firstName: '',
    lastName: '',
    jobClass_id: '',
    uptime: '',
    casesPerHour: '',
    palletsPerHour: '',
    attendance: '',
    department: '',
    hireDate: '',
  };

  // onSubmit function with Redux dispatch
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    // Prepare the metrics object
    let metrics = [];
    const metricNames = {
      uptime: 'Uptime',
      casesPerHour: 'Cases Per Hour',
      palletsPerHour: 'Pallets Per Hour',
      attendance: 'Attendance',
    };

    Object.keys(metricNames).forEach((key) => {
      if (values[key]) {
        metrics.push({
          name: metricNames[key],
          value: values[key],
        });
      }
    });

    // Create the payload to send to the server
    const payload = {
      firstName: values.firstName,
      lastName: values.lastName,
      jobClass_id: parseInt(values.jobClass_id, 10),
      metrics: metrics,
      department: values.department,
      hireDate: values.hireDate,
    };

    // Dispatching the action with form data
    dispatch(addWorker(payload))
      .unwrap()
      .then((addedWorker) => {
        resetForm();
      })
      .catch((error) => {
        console.error('Form submission error:', error);
      })
      .finally(() => {
        setSubmitting(false);
      });
  };

  return (
    <div>
      <h3>Add an Associate!</h3>
      <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
        {({ isSubmitting, setFieldValue, values, errors, handleChange }) => (
          <Form>
            <MySelect label="Job Class" name="jobClass_id" options={[ /* Define your job class options here */ ]} onChange={(e) => handleJobClassChange(setFieldValue, e.target.value)}>
              <option value="">Select a Job Class</option>
              {/* Render job class options */}
            </MySelect>

            <MySelect label="Department" name="department" options={uniqueDepartments.map(department => ({ value: department, label: department }))} onChange={handleChange}>
              <option value="">Select a Department</option>
              {/* Render department options */}
            </MySelect>

            <MyTextInput label="Hire Date" name="hireDate" type="date" onChange={handleChange} />

            {/* Render input fields for selected job class */}

            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default AssociateForm;
