import React from 'react';
import { Formik, Form, useField } from 'formik';
import { useDispatch } from 'react-redux';
import { addWorker } from '../features/workersSlice';


// Define the MySelect component
const MySelect = ({ label, ...props }) => {
  const [field, meta] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <select {...field} {...props} />
      {meta.touched && meta.error ? <div className="error">{meta.error}</div> : null}
    </>
  );
};

// Define the jobClasses array
const jobClasses = [
  { id: 1, name: 'Putaway Forklift' },
  { id: 2, name: 'Selector' },
  // ... other job classes ...
];

// Custom Input component for Formik
const MyTextInput = ({ label, ...props }) => {
  const [field] = useField(props);
  return (
    <>
      <label htmlFor={props.id || props.name}>{label}</label>
      <input {...field} {...props} />
    </>
  );
};

function AssociateForm() {
  const dispatch = useDispatch();

  // Initial values for the form
  const initialValues = {
    firstName: '',
    lastName: '',
    uptime: '',
    casesPerHour: '',
    palletsPerHour: '',
    attendance: '',
    jobClass_id: '', // Make sure this is included
  };

  // onSubmit function with Redux dispatch
  const onSubmit = (values, { setSubmitting, resetForm }) => {
    console.log('Attempting to submit form with values:', values);

    // Dispatching the action with form data
    dispatch(addWorker(values))
      .unwrap()
      .then(addedWorker => {
        console.log('Form submission successful, added worker:', addedWorker);
        resetForm();
      })
      .catch(error => {
        console.error('Form submission error:', error);
      })
      .finally(() => {
        setSubmitting(false);
      });
  };

  return (
    <div>
      <h3>Add an Associate!</h3>
      <Formik
        initialValues={initialValues}
        // ... validation schema and onSubmit function ...
      >
        {({ isSubmitting }) => (
          <Form>
            <MySelect label="Job Class" name="jobClass_id">
              <option value="">Select a Job Class</option>
              {jobClasses.map(jobClass => (
                <option key={jobClass.id} value={jobClass.id}>
                  {jobClass.name}
                </option>
              ))}
            </MySelect>
            {/* ... other input fields ... */}
            <button type="submit" disabled={isSubmitting}>
              Submit
            </button>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default AssociateForm;
