import React, { useState, useEffect } from 'react';
import { Button, Form } from 'semantic-ui-react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchSchedule } from '../features/scheduleBuildSlice';

function ScheduleForm() {
  const [inputValues, setInputValues] = useState({
    Monday: 0,
    Tuesday: 0,
    Wednesday: 0,
    Thursday: 0,
    Friday: 0,
    Saturday: 0,
    Sunday: 0
  });
  const dispatch = useDispatch();
  const schedule = useSelector((state) => state.schedule.data);

  useEffect(() => {
    dispatch(fetchSchedule(inputValues));
  }, [dispatch]);

  const handleSliderChange = (name, value) => {
    setInputValues({ ...inputValues, [name]: value });
  };

  const handleSubmit = () => {
    dispatch(fetchSchedule(inputValues));
  };

  // ... (rest of the component, including JSX for Form and Table)

  return (
    <div>
      <Form>
        {/* Generate input fields for each day */}
        {Object.keys(inputValues).map((day) => (
          <Form.Field key={day}>
            <label>{day} Needs (heads)</label>
            <input
              type='range'
              min={0}
              max={100}
              value={inputValues[day]}
              onChange={(e) => handleSliderChange(day, parseInt(e.target.value))}
            />
          </Form.Field>
        ))}
      </Form>
      <Button onClick={handleSubmit} primary>
        Generate Schedule
      </Button>
      {/* Display schedule table and results */}
      {/* ... */}
    </div>
  );
}

export default ScheduleForm;
