import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchWorkers } from '../features/workersSlice';

const AssociatesTable = () => {
  const dispatch = useDispatch();
  const { workers, status, error } = useSelector(state => state.workers);
  const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

  useEffect(() => {
    if (status === 'idle') {
      dispatch(fetchWorkers());
    }
  }, [status, dispatch]);

  const renderScheduleForDay = (schedules, day) => {
    const schedule = schedules[day];
    return schedule ? `${schedule.shift_start} - ${schedule.shift_end}` : 'Off';
  };

  if (status === 'loading') {
    return <div>Loading...</div>;
  } else if (status === 'failed') {
    return <div>Error: {error}</div>;
  }

  return (
    <div>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Department</th>
            {daysOfWeek.map(day => (
              <th key={day}>{day}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {workers.map((worker) => (
            <tr key={worker.id}>
              <td>{`${worker.first_name} ${worker.last_name}`}</td>
              <td>{worker.department.name}</td>
              {daysOfWeek.map(day => (
                <td key={day}>
                  {renderScheduleForDay(worker.schedules, day)}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AssociatesTable;
