import React from 'react';
import { useSelector } from 'react-redux';

function ScheduleTable() {
  const scheduleState = useSelector((state) => state.schedule);

  // Check for the loading and error states
  if (scheduleState.status === 'loading') {
    return <p>Loading...</p>;
  }
  if (scheduleState.status === 'failed') {
    return <p>Error: {scheduleState.error}</p>;
  }

  // Ensure that schedule data is available before trying to access it
  if (!scheduleState.data || !scheduleState.data.schedule) {
    return <p>No schedule data available.</p>;
  }

  return (
    <table>
      <thead>
        <tr>
          <th>Day</th>
          <th>Staff Needed</th>
        </tr>
      </thead>
      <tbody>
        {Object.entries(scheduleState.data.schedule).map(([day, staffCount]) => (
          <tr key={day}>
            <td>{day}</td>
            <td>{staffCount}</td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default ScheduleTable;
