import React from 'react'
import { Card } from 'semantic-ui-react'
import { useDispatch } from 'react-redux'
import { deleteWorker } from '../features/workersSlice'

function AssociateCard({ worker }) {
  const dispatch = useDispatch();

  function handleWorkerDelete() {
    fetch(`http://localhost:5555/selectors_metrics/${worker.id}`, {
      method: 'DELETE'
    })
      .then(r => r.json())
      .then(data => {
        dispatch(deleteWorker(worker.id));
      })
  }

  return (
    <Card style={{ padding: '10px', margin: '10px', fontSize: '0.8em' }}>
      <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
        {/* <div className="image">
          <img className="worker-avatar" src={worker.image}alt="" />
        </div> */}
        <div className="content" style={{ marginBottom: '5px' }}>
          <div className="header">{worker.name}</div>
        </div>
        <div className="extra content">
          <span>
            <div>Uptime {worker.uptime}</div>
            <div>Cases Per Hour {worker.casesPerHour}</div>
            <div>Attendance {worker.attendance}</div>
            <button onClick={handleWorkerDelete} style={{ marginTop: '5px' }}>ðŸ—‘</button>
          </span>
        </div>
      </div>
    </Card>
  );
}

export default AssociateCard;
